PKIMessage.rightHandSide:
SEQUENCE {
   header           PKIHeader,
   body             PKIBody,
   protection   [0] PKIProtection OPTIONAL,
   extraCerts   [1] SEQUENCE SIZE (1..MAX) OF CMPCertificate
               OPTIONAL }

DHBMParameter.rightHandSide:
 SEQUENCE {
    owf                 AlgorithmIdentifier{DIGEST-ALGORITHM, {...}},
    -- AlgId for a One-Way Function (SHA-1 recommended)
    mac                 AlgorithmIdentifier{MAC-ALGORITHM, {...}}
    -- the MAC AlgId (e.g., DES-MAC, Triple-DES-MAC [PKCS11],
    -- or HMAC [RFC2104, RFC2202])
}

PKIStatusInfo.rightHandSide:
SEQUENCE {
   status        PKIStatus,
   statusString  PKIFreeText     OPTIONAL,
   failInfo      PKIFailureInfo  OPTIONAL }

OOBCertHash.rightHandSide:
 SEQUENCE {
    hashAlg     [0] AlgorithmIdentifier{DIGEST-ALGORITHM, {...}}
                        OPTIONAL,
    certId      [1] CertId                  OPTIONAL,
    hashVal         BIT STRING
    -- hashVal is calculated over the DER encoding of the
    -- self-signed certificate with the identifier certID.
}

CertRepMessage.rightHandSide:
SEQUENCE {
   caPubs       [1] SEQUENCE SIZE (1..MAX) OF CMPCertificate
               OPTIONAL,
   response         SEQUENCE OF CertResponse }

CertifiedKeyPair.rightHandSide:
SEQUENCE {
   certOrEncCert       CertOrEncCert,
   privateKey      [0] EncryptedValue      OPTIONAL,
   -- see [RFC4211] for comment on encoding
   publicationInfo [1] PKIPublicationInfo  OPTIONAL }

CAKeyUpdAnnContent.rightHandSide:
 SEQUENCE {
    oldWithNew   CMPCertificate, -- old pub signed with new priv
    newWithOld   CMPCertificate, -- new pub signed with old priv
    newWithNew   CMPCertificate  -- new pub signed with new priv
}

PollRepContent.rightHandSide:
SEQUENCE OF SEQUENCE {
   certReqId              INTEGER,
   checkAfter             INTEGER,  -- time in seconds
   reason                 PKIFreeText OPTIONAL }
